	editor.ui.registry.addButton('dlemp', {
		tooltip : 'Insert video (BB Codes)',
		text : '<i class="mce-i-dleicon icon-video"></i>',

		onAction : function() {

			var win = {
                title: 'Insert video (BB Codes)',

  				body: {
    				type: 'panel', // The root body type - a Panel or TabPanel
				    items: [ // A list of panel components
							{
				              type: 'input',
				              name: 'href',
				              label: 'Url'
				            },
				            {type: 'input', 
								name: 'descr', 
								label: 'Description', 
							},
				            {type: 'input', 
								name: 'poster', 
								label: 'Link to poster', 
							}
				          ]
				},

				buttons: [ // A list of footer buttons
				    {
				      type: 'cancel',
				      text: 'Close'
				    },
				    {
				      type: 'custom',
				      name: 'addbtn',
				      primary: true,
				      text: 'Add to Playlist'
				    },

				    {
				      type: 'submit',
				      name: 'submitbtn',
				      primary: true,
				      text: 'Insert'
				    }
				  ],

				onAction: function(dialogApi, details) {

					var data = dialogApi.getData();

					var videourl = data.href;
					var videoposter = data.poster;
					var videodescr = data.descr;
								
					var videolink = videourl;

					if (videoposter != "" || videodescr != "" ) { 
						videolink += '|' + videoposter;
					}
								
					if (videodescr != "" ) { 
						videolink += '|' + videodescr;
					}

					if (videolink != "") {
						playlist +=  videolink + ',';
					}
								
					dialogApi.redial(win);
				},

                onSubmit: function(dialogApi) {

					var data = dialogApi.getData();
					
					var videolink = data.href;
					var poster = data.poster;
					var descr = data.descr;

					if (poster != "" || descr != "" ) { 
						videolink += '|' + poster;
					}
					
					if (descr != "" ) { 
						videolink += '|' + descr;
					}
					
					if(videolink != "" && videolink != "https://") {
						playlist += videolink;
					}else if(playlist != "") {
						playlist = playlist.substring(0, playlist.length - 1)
					}

					if(playlist != "") {
                   		editor.insertContent('[video=' + playlist +']');
                   	}

					playlist = '';
					dialogApi.close();
                }
            };

           editor.windowManager.open( win );

		}

    });

