	editor.ui.registry.addButton('dleimage', {
		tooltip : 'Insert image',
		icon : 'image',

		onAction : function() {
			editor.windowManager.open({
                title: 'Insert image',

   				body: {
    				type: 'panel', // The root body type - a Panel or TabPanel
				    items: [ // A list of panel components
							{
				                type: 'input',
				                name: 'href',
				                label: 'Url'
				            },
							{
				                type: 'input',
				                name: 'text',
				                label: 'Image description'
				            },
				            {
			                    type: 'listbox',
			                    name: 'align',
			                    label: 'Alignment',

								  items: [
								    { text: 'None', value: '' },
								    { text: 'Left', value: 'left' },
								    { text: 'Right', value: 'right' },
								    { text: 'Center', value: 'center' }
								    ]

			                }
				      ]
				    },
				  buttons: [ // A list of footer buttons
				    {
				      type: 'cancel',
				      text: 'Cancel'
				    },
				    {
				      type: 'submit',
				      primary: true,
				      text: 'Insert'
				    }
				  ],

                onSubmit: function(e) {
                   		var data = e.getData();

						var imageurl = data.href;
						var imagealt = data.text;
						var imagealign = data.align;
						
						var imgoption = "";
				
						if (imagealt != "") { 
				
							imgoption = "|"+imagealt;
				
						}
				
						if (imagealign != "" && imagealign != "center") { 
				
							imgoption = imagealign+imgoption;
				
						}
				
						if (imgoption != "" ) {
				
							imgoption = "="+imgoption;
				
						}
				
						if (imagealign == "center") {
							editor.insertContent('<p style="text-align: center;">[img'+imgoption+']'+imageurl+'[/img]</p>');
						}
						else {
							editor.insertContent("[img"+imgoption+"]"+imageurl+"[/img]", "", false);
						}

						e.close();

                }

            });
		}
    });



